    var piclist = new Array();
    for (var i = 0; i < list.length; i++) {
        var v = list[i];
        var s = v;
        if (v.substring(0, 1) == "/") {
            s = qTcms_m_weburl + s;
        } else {
            if (qTcms_Pic_m_if != "2") {
                v = v.replaceAll("\\?", "a1a1");
                v = v.replaceAll("&", "b1b1");
                v = v.replaceAll("%", "c1c1");
                var m_httpurl = "";
                if (typeof(qTcms_S_m_mhttpurl) != "undefined") m_httpurl = java.base64Decode(qTcms_S_m_mhttpurl);
            }
        }
        piclist[i] =s;
    }
    return piclist;
}()).map(uri=>
'<img src="'+uri+'">').join("\n")

